document.addEventListener("DOMContentLoaded",function(){const lightboxHtml=<div id="lightbox" class="lightbox"><span class="lightbox-close" onclick="closeLightbox()">&times;</span><img class="lightbox-content" id="lightbox-img"/></div>;document.body.insertAdjacentHTML('beforeend',lightboxHtml);const lightbox=document.getElementById("lightbox");lightbox.addEventListener("click",function(){closeLightbox();});});function openLightbox(imageElement){const lightbox=document.getElementById("lightbox");const lightboxImg=document.getElementById("lightbox-img");const imageSrc=imageElement.getAttribute("data-src")||imageElement.src;lightboxImg.src=imageSrc;lightbox.style.display="flex";}function closeLightbox(){const lightbox=document.getElementById("lightbox");lightbox.style.display="none";}
