let currentIndex=0,slides=document.querySelectorAll('.slide'),dots=document.querySelectorAll('.dot'),totalSlides=slides.length;function showSlide(e){document.querySelector('.slider-wrapper').style.transform=`translateX(${-e*710}px)`;dots.forEach(e=>e.classList.remove('active-dot'));dots[e].classList.add('active-dot')}function changeSlide(e){currentIndex=(currentIndex+e+totalSlides)%totalSlides;showSlide(currentIndex)}function setSlide(e){currentIndex=e;showSlide(currentIndex)}document.addEventListener('DOMContentLoaded',()=>showSlide(currentIndex));