const slider=document.querySelector('.slider'),slides=document.querySelectorAll('.slide'),prevBtn=document.querySelector('.prev'),nextBtn=document.querySelector('.next'),dots=document.querySelectorAll('.dot'),sliderContainer=document.querySelector('.slider-container');let currentIndex=0,autoSlideInterval;function updateDots(){dots.forEach((dot,index)=>{index===currentIndex?dot.classList.add('active'):dot.classList.remove('active')})}function showSlides(index){currentIndex=index>=slides.length?0:index<0?slides.length-1:index;slider.style.transform=`translateX(-${currentIndex*100}%)`;updateDots()}function nextSlide(){showSlides(currentIndex+1)}function prevSlide(){showSlides(currentIndex-1)}function startAutoSlide(){autoSlideInterval=setInterval(nextSlide,4000)}function stopAutoSlide(){clearInterval(autoSlideInterval)}dots.forEach(dot=>{dot.addEventListener('click',()=>{stopAutoSlide(),showSlides(parseInt(dot.dataset.index)),startAutoSlide()})}),nextBtn.addEventListener('click',nextSlide),prevBtn.addEventListener('click',prevSlide),sliderContainer.addEventListener('mouseover',stopAutoSlide),sliderContainer.addEventListener('mouseout',startAutoSlide),startAutoSlide(),updateDots();function openLightbox(imageElement){const lightbox=document.getElementById('lightbox'),lightboxImg=document.getElementById('lightbox-img');lightboxImg.src=imageElement.getAttribute('data-src'),lightbox.style.display='flex',lightbox.onclick=function(){closeLightbox()}}function closeLightbox(){document.getElementById('lightbox').style.display='none'}