const slider=document.querySelector('.slider'),slides=document.querySelectorAll('.slide'),prevBtn=document.querySelector('.prev'),nextBtn=document.querySelector('.next'),dots=document.querySelectorAll('.dot'),sliderContainer=document.querySelector('.slider-container');let currentIndex=0,autoSlideInterval;function updateDots(){dots.forEach((e,t)=>{t===currentIndex?e.classList.add('active'):e.classList.remove('active')})}function showSlides(e){currentIndex=e>=slides.length?0:e<0?slides.length-1:e,slider.style.transform=`translateX(-${currentIndex*100}%)`,updateDots()}function nextSlide(){showSlides(currentIndex+1)}function prevSlide(){showSlides(currentIndex-1)}function startAutoSlide(){autoSlideInterval=setInterval(nextSlide,4e3)}function stopAutoSlide(){clearInterval(autoSlideInterval)}dots.forEach(e=>{e.addEventListener('click',()=>{stopAutoSlide(),showSlides(parseInt(e.dataset.index)),startAutoSlide()})}),nextBtn.addEventListener('click',nextSlide),prevBtn.addEventListener('click',prevSlide),sliderContainer.addEventListener('mouseover',stopAutoSlide),sliderContainer.addEventListener('mouseout',startAutoSlide),startAutoSlide(),updateDots();function openLightbox(e){const t=document.getElementById('lightbox'),n=document.getElementById('lightbox-img');n.src=e.getAttribute('data-src'),t.style.display='flex',t.onclick=function(){closeLightbox()}}function closeLightbox(){document.getElementById('lightbox').style.display='none'}