let currentIndex=0;const slides=document.querySelectorAll('.slide'),dots=document.querySelectorAll('.dot'),slideWidth=710;function showSlide(index){currentIndex=index>=slides.length?0:index<0?slides.length-1:index;document.querySelector('.slider-wrapper').style.transform=`translateX(${-currentIndex*slideWidth}px)`;dots.forEach(dot=>dot.classList.remove('active-dot'));dots[currentIndex].classList.add('active-dot');}function nextSlide(){showSlide(currentIndex+1);}function prevSlide(){showSlide(currentIndex-1);}function currentSlide(index){showSlide(index);}document.addEventListener('DOMContentLoaded',()=>showSlide(currentIndex));function openLightbox(img){const lb=document.getElementById('lightbox'),lbImg=document.getElementById('lightbox-img');lbImg.src=img.dataset.src;lb.style.display='flex';lb.onclick=function(){closeLightbox();};}function closeLightbox(){document.getElementById('lightbox').style.display='none';}